import React, { useState } from "react";
import axios from "axios";

function App() {
  const [dataFile, setDataFile] = useState(null);
  const [skuFile, setSkuFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState("");

  const handleSubmit = async () => {
    if (!dataFile || !skuFile) {
      alert("Please upload both files.");
      return;
    }

    setLoading(true);
    setProgress(0);
    setStatus("Uploading...");

    const formData = new FormData();
    formData.append("data_file", dataFile);
    formData.append("sku_file", skuFile);

    try {
      const response = await axios.post("http://localhost:8000/filter-excel/", formData, {
        responseType: "blob",
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const percent = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setProgress(percent);
          }
        },
      });

      setStatus("Processing on server...");

      // Download result
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "Filtered_Result.xlsx");
      document.body.appendChild(link);
      link.click();

      setStatus("Download complete!");
    } catch (error) {
      setStatus("Error: " + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
      setProgress(100);
    }
  };

  return (
    <div style={{ margin: "50px", maxWidth: "500px" }}>
      <h2>Excel SKU Filter</h2>

      <label>Upload Excel/Zip file:</label>
      <input type="file" onChange={(e) => setDataFile(e.target.files[0])} />

      <br /><br />

      <label>Upload SKU file:</label>
      <input type="file" onChange={(e) => setSkuFile(e.target.files[0])} />

      <br /><br />

      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "Processing..." : "Filter & Download"}
      </button>

      {loading && (
        <div style={{ marginTop: "20px" }}>
          <div style={{ height: "25px", width: "100%", background: "#eee", borderRadius: "5px" }}>
            <div
              style={{
                height: "100%",
                width: `${progress}%`,
                background: "#4caf50",
                borderRadius: "5px",
                textAlign: "center",
                color: "white",
                transition: "width 0.3s",
              }}
            >
              {progress}%
            </div>
          </div>
          <p>{status}</p>
        </div>
      )}
    </div>
  );
}

export default App;
