import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [dataFile, setDataFile] = useState(null);
  const [skuFile, setSkuFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState("");

  // üîπ Backend URL (update if running on server)
  const BASE_URL = "http://localhost:8000";

  const handleSubmit = async () => {
    if (!dataFile || !skuFile) {
      alert("Please upload both files.");
      return;
    }

    setLoading(true);
    setProgress(0);
    setStatus("üì§ Uploading files...");

    const formData = new FormData();
    formData.append("data_file", dataFile);
    formData.append("sku_file", skuFile);

    try {
      const response = await axios.post(`${BASE_URL}/filter-excel/`, formData, {
        responseType: "blob",
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const percent = Math.round(
              (progressEvent.loaded * 100) / progressEvent.total
            );
            setProgress(percent);
            setStatus(`üì§ Uploading... ${percent}%`);
          }
        },
      });

      setStatus("‚öôÔ∏è Processing on server...");

      // üîπ Download result
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "Filtered_Result.xlsx");
      document.body.appendChild(link);
      link.click();
      link.remove();

      setStatus("‚úÖ Download complete!");
      setDataFile(null);
      setSkuFile(null);
    } catch (error) {
      let errorMsg = "‚ùå Error: Something went wrong.";
      if (error.response && error.response.data) {
        const reader = new FileReader();
        reader.onload = () => {
          try {
            const errJson = JSON.parse(reader.result);
            errorMsg = "‚ùå Error: " + (errJson.error || error.message);
            setStatus(errorMsg);
          } catch {
            setStatus(errorMsg);
          }
        };
        reader.readAsText(error.response.data);
      } else {
        setStatus(errorMsg);
      }
    } finally {
      setLoading(false);
      setProgress(100);
    }
  };

  return (
    <div className="app-container">
      <div className="card">
        <h2 className="title">Excel SKU Filter</h2>

        <div className="input-group">
          <label>Upload Excel/Zip file:</label>
          <input
            type="file"
            onChange={(e) => setDataFile(e.target.files[0])}
          />
        </div>

        <div className="input-group">
          <label>Upload SKU file:</label>
          <input
            type="file"
            onChange={(e) => setSkuFile(e.target.files[0])}
          />
        </div>

        <button className="btn" onClick={handleSubmit} disabled={loading}>
          {loading ? "Processing..." : "Filter & Download"}
        </button>

        {loading && (
          <div className="progress-container">
            <div className="progress-bar" style={{ width: `${progress}%` }}>
              {progress}%
            </div>
          </div>
        )}
        {status && <p className="status-text">{status}</p>}
      </div>
    </div>
  );
}

export default App;
