import React, { useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";

export default function App() {
  const [dataFile, setDataFile] = useState(null);
  const [skuFile, setSkuFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [downloading, setDownloading] = useState(false);

  const handleUpload = async () => {
    if (!dataFile || !skuFile) {
      alert("Please select both Data file (Excel/ZIP) and SKU file (Excel).");
      return;
    }

    const formData = new FormData();
    formData.append("data_file", dataFile);
    formData.append("sku_file", skuFile);

    setProgress(0);
    setDownloading(true);

    try {
      const response = await axios.post("http://localhost:8000/filter-excel/", formData, {
        responseType: "blob",
        onUploadProgress: (progressEvent) => {
          // realistic progress simulation
          const percent = Math.min(95, Math.round((progressEvent.loaded * 100) / progressEvent.total));
          setProgress(percent);
        },
      });

      // Artificial delay for smooth progress animation
      setTimeout(() => {
        setProgress(100);
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "Filtered_Result.xlsx");
        document.body.appendChild(link);
        link.click();
        link.remove();
        setDownloading(false);
      }, 500);
    } catch (error) {
      console.error("Upload failed", error);
      setDownloading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 p-6">
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-lg"
      >
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">
          Excel SKU Filter
        </h1>

        <div className="space-y-4">
          {/* Data file upload */}
          <div>
            <label className="block font-medium text-gray-600">Data File (Excel/ZIP)</label>
            <input
              type="file"
              onChange={(e) => setDataFile(e.target.files[0])}
              className="w-full mt-2 p-2 border rounded-lg focus:ring focus:ring-indigo-300"
            />
          </div>

          {/* SKU file upload */}
          <div>
            <label className="block font-medium text-gray-600">SKU File (Excel)</label>
            <input
              type="file"
              onChange={(e) => setSkuFile(e.target.files[0])}
              className="w-full mt-2 p-2 border rounded-lg focus:ring focus:ring-indigo-300"
            />
          </div>

          {/* Upload button */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleUpload}
            disabled={downloading}
            className="w-full bg-indigo-600 text-white py-3 rounded-xl font-semibold shadow-lg hover:bg-indigo-700 transition duration-300"
          >
            {downloading ? "Processing..." : "Upload & Filter"}
          </motion.button>

          {/* Progress Bar */}
          {downloading && (
            <div className="mt-4">
              <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden">
                <motion.div
                  initial={{ width: "0%" }}
                  animate={{ width: `${progress}%` }}
                  transition={{ ease: "easeOut", duration: 0.5 }}
                  className="bg-green-500 h-4 rounded-full"
                />
              </div>
              <p className="text-center text-sm text-gray-600 mt-1">{progress}%</p>
            </div>
          )}
        </div>
      </motion.div>
    </div>
  );
}
