import React, { useState } from "react";
import axios from "axios";
import "./App.css";   // Import CSS file

function App() {
  const [dataFile, setDataFile] = useState(null);
  const [skuFile, setSkuFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState("");

  const handleSubmit = async () => {
    if (!dataFile || !skuFile) {
      alert("Please upload both files.");
      return;
    }

    setLoading(true);
    setProgress(0);
    setStatus("Uploading...");

    const formData = new FormData();
    formData.append("data_file", dataFile);
    formData.append("sku_file", skuFile);

    try {
      const response = await axios.post("http://localhost:8000/filter-excel/", formData, {
        responseType: "blob",
        onUploadProgress: (progressEvent) => {
          if (progressEvent.total) {
            const percent = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setProgress(percent);
          }
        },
      });

      setStatus("Processing on server...");

      // Download result
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "Filtered_Result.xlsx");
      document.body.appendChild(link);
      link.click();

      setStatus("✅ Download complete!");
    } catch (error) {
      setStatus("❌ Error: " + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
      setProgress(100);
    }
  };

  return (
    <div className="app-container">
      <div className="card">
        <h2 className="title">Excel SKU Filter</h2>

        <div className="input-group">
          <label>Upload Excel/Zip file:</label>
          <input type="file" onChange={(e) => setDataFile(e.target.files[0])} />
        </div>

        <div className="input-group">
          <label>Upload SKU file:</label>
          <input type="file" onChange={(e) => setSkuFile(e.target.files[0])} />
        </div>

        <button className="btn" onClick={handleSubmit} disabled={loading}>
          {loading ? "Processing..." : "Filter & Download"}
        </button>

        {loading && (
          <div className="progress-container">
            <div className="progress-bar" style={{ width: `${progress}%` }}>
              {progress}%
            </div>
            <p className="status-text">{status}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
