import React, { useState } from "react";
import axios from "axios";

function App() {
  const [dataFile, setDataFile] = useState(null);
  const [skuFile, setSkuFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!dataFile || !skuFile) {
      alert("Please upload both files.");
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append("data_file", dataFile);
    formData.append("sku_file", skuFile);

    try {
      const response = await axios.post("http://localhost:8000/filter-excel/", formData, {
        responseType: "blob",
      });

      // Download result
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "Filtered_Result.xlsx");
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      alert("Error: " + error.response?.data?.error || error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ margin: "50px" }}>
      <h2>Excel SKU Filter</h2>

      <label>Upload Excel/Zip file:</label>
      <input type="file" onChange={(e) => setDataFile(e.target.files[0])} />

      <br /><br />

      <label>Upload SKU file:</label>
      <input type="file" onChange={(e) => setSkuFile(e.target.files[0])} />

      <br /><br />

      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "Processing..." : "Filter & Download"}
      </button>
    </div>
  );
}

export default App;
